mapper.select:
SELECT 

FROM teacher_taxpayer_form_detail a
WHERE a.id = #{id}

SELECT 

FROM teacher_taxpayer_form_detail a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.taxpayer_form_id = #{taxpayerFormId}


		AND a.teacher_id = #{teacherId}


		AND a.form_type = #{formType}


		AND a.uploader = #{uploader}




		order by a.create_time desc

mapper.update:
UPDATE teacher_taxpayer_form_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

